package Practica;

public class practicaBooleanos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 6;
		int y = 4;
		
		boolean menorQue = (x<y);
		System.out.println("6<4: "+menorQue);
		
		boolean mayorQue = (x>y);
		System.out.println("6>4: "+mayorQue);
		
		boolean menorOIgualQue = (x<=y);
		System.out.println("6<=4: "+menorOIgualQue);
		
		boolean mayorOIgualQue = (x>=y);
		System.out.println("6>=4: "+mayorOIgualQue);
		
		boolean igualA = (x==y);
		System.out.println("6==4: "+igualA);
		
		boolean diferenteDe = (x!=y);
		System.out.println("6!=4: "+diferenteDe);
		
		

	}

}